    int[] comparissons = new int[]{ Calendar.YEAR, Calendar.MONTH, Calendar.DATE };
    for (int i = 0; i < (comparissons.length); i++) {
        switch (module(((this.get(comparissons[i])) - (when.get(comparissons[i]))))) {
            case -1 :
                return -1;
            case 1 :
                return 1;
            default :
                break;
        }
    }
    return 0;
}

