/**
 * Simple write information of tasks
 */
public void writeTask(Writer fout, DefaultMutableTreeNode node) {
    ArrayList lot = new ArrayList();
    try {
        GanttTask task = ((GanttTask) (node.getUserObject()));
        if ((task.getTaskID()) == (-1))
            throw new RuntimeException("A task can not has a number equal to -1");

        int id = task.getTaskID();
        /* if (id >= lot.size()) { return; } */
        boolean haschild = false;
        ArrayList child = myTree.getAllChildTask(node);
        if ((child.size()) != 0) {
            haschild = true;
        }
        // Writes data of task
        fout.write((((((s) + (s)) + "<previous-task id="") + (task.getTaskID()))// lots.indexOf(task.toString())
         + // +
        // //By
        // CL
        """));
        fout.write(((" start="" + (task.getStart().toXMLString())) + """));
        fout.write(((" duration="" + (task.getLength())) + """));
        fout.write(((" meeting="" + (task.isMilestone())) + """));
        fout.write(((" super="" + haschild) + """));
        fout.write("/>
");
        // Write the child of the task
        if (haschild) {
            for (int i = 0; i < (child.size()); i++) {
                Task task2 = ((Task) (((DefaultMutableTreeNode) (child.get(i))).getUserObject()));
                int newid = -1;// lot.lastIndexOf(task2);

                for (int j = 0; j < (lot.size()); j++) {
                    String a = task2.toString();
                    if (a == null)
                        System.out.println("nul");

                    String b = lot.get(j).toString();
                    if (a.equals(b)) {
                        newid = j;
                    }
                }
                writeTask(fout, ((DefaultMutableTreeNode) (child.get(i))));
            }
        }
        // end of task section
    } catch (Exception e) {
        if (!(GPLogger.log(e))) {
            e.printStackTrace(System.err);
        }
    }
}

private void writeTasks() {
    Enumeration children = ((DefaultMutableTreeNode) (myTree.getJTree().getModel().getRoot())).children();
    write(((((s) + "<previous-tasks name="") + (myName)) + "">
"));
    while (children.hasMoreElements()) {
        DefaultMutableTreeNode element = ((DefaultMutableTreeNode) (children.nextElement()));
        /* lot.indexOf(element) */
        writeTask(os, element);
    } 
    write(((s) + "</previous-tasks>"));
}

public void writeTasksFromLoaded(ArrayList tasks) throws IOException {
    write(((((s) + "<previous-tasks name="") + (myName)) + "">
"));
    for (int i = 0; i < (tasks.size()); i++) {
        os.write((((((s) + (s)) + "<previous-task id="") + (((GanttPreviousStateTask) (tasks.get(i))).getId())) + """));
        os.write(((" start="" + (((GanttPreviousStateTask) (tasks.get(i))).getStart().toXMLString())) + """));
        os.write(((" duration="" + (((GanttPreviousStateTask) (tasks.get(i))).getDuration())) + """));
        os.write(((" meeting="" + (((GanttPreviousStateTask) (tasks.get(i))).isMilestone())) + """));
        os.write(((" super="" + (((GanttPreviousStateTask) (tasks.get(i))).hasNested())) + """));
        os.write("/>
");
    }
    write(((s) + "</previous-tasks>"));
}

public GanttPreviousStateTask(int id, GanttCalendar start, int duration, boolean isMilestone, boolean hasNested) {
    myId = id;
    myStart = start;
    myDuration = duration;
    this.isMilestone = isMilestone;
    this.hasNested = hasNested;
}

public int getDuration() {
    return myDuration;
}

public GanttCalendar getEnd(GPCalendar calendar) {
    int duration = myDuration;
    GanttCalendar end = myStart.newAdd(myDuration);
    for (int i = 0; i < duration; i++) {
        // if (myId == 1)
        // System.out.println (myStart.newAdd(i).getTime() + " is workingDay : " + calendar.isNonWorkingDay(myStart.newAdd(i).getTime()));
        if (calendar.isNonWorkingDay(myStart.newAdd(i).getTime())) {
            end.add(1);
            duration++;
        }
    }
    return end;
}

