    for (int i = 0; i < (selectedNodes.length); i++) {
        if ((selectedNodes[i]) instanceof ResourceNode) {
            count++;
        }
    }
    this.clipboard = new ProjectResource[count];
    int index = 0;
    for (int i = 0; i < (selectedNodes.length); i++) {
        if ((selectedNodes[i]) instanceof ResourceNode) {
            ResourceNode rn = ((ResourceNode) (selectedNodes[i]));
