            boolean isNodeDescendant = false;
            for (int j = 0; j < (cpNodesArrayList.size()); j++) {
                if (((DefaultMutableTreeNode) (cdmtn[i])).isNodeDescendant(((DefaultMutableTreeNode) (cpNodesArrayList.get(j))))) {
