    Collections.sort(hiddenTask);
    for (int i = 0; i < (hiddenTask.size()); i++) {
        GanttTree2.HiddenTask ht = ((GanttTree2.HiddenTask) (hiddenTask.get(i)));
        DefaultMutableTreeNode node = ht.getNode();
