 *
 *
 * @unknown 
 */
public boolean isCellEditable(Object node, int column) {
    return ((node instanceof ResourceNode) && ((((column == (ResourceTreeTableModel.INDEX_RESOURCE_EMAIL)) || (column == (ResourceTreeTableModel.INDEX_RESOURCE_NAME))) || (column == (ResourceTreeTableModel.INDEX_RESOURCE_PHONE))) || (column == (ResourceTreeTableModel.INDEX_RESOURCE_ROLE)))) || (((node instanceof AssignmentNode) && (column == (ResourceTreeTableModel.INDEX_RESOURCE_ROLE_TASK))) || /* assumes the INDEX_RESOURCE_ROLE_TASK is the last mandatory column */
    (column > (ResourceTreeTableModel.INDEX_RESOURCE_ROLE_TASK)));
}

public boolean moveDown(HumanResource resource) {
    myResourceManager.down(resource);
    ResourceNode rn = getNodeForResource(resource);
    int index = root.getIndex(root.getChildAfter(rn));
    removeNodeFromParent(rn);
    insertNodeInto(rn, root, index);
    return true;
}

/**
 * Move Up the selected resource
 */
public boolean moveUp(HumanResource resource) {
    myResourceManager.up(resource);
    ResourceNode rn = getNodeForResource(resource);
    int index = root.getIndex(root.getChildBefore(rn));
    removeNodeFromParent(rn);
    insertNodeInto(rn, root, index);
    return true;
}

/**
 * Message this to remove node from its parent. This will message
 * nodesWereRemoved to create the appropriate event. This is the preferred
 * way to remove a node as it handles the event creation for you.
 */
public void removeNodeFromParent(MutableTreeNode node) {
    if (node != null) {
        MutableTreeNode parent = ((MutableTreeNode) (node.getParent()));
        if (parent == null)
            throw new IllegalArgumentException("node does not have a parent.");

        int[] childIndex = new int[1];
        Object[] removedArray = new Object[1];
        childIndex[0] = parent.getIndex(node);
        parent.remove(childIndex[0]);
        removedArray[0] = node;
        nodesWereRemoved(parent, childIndex, removedArray);
    }
}

