    // if ((LabelPositionOptionImpl.UP.equals(nextOption.getValue()))) {
    // textUP = true;
    // }
    // if ((LabelPositionOptionImpl.DOWN.equals(nextOption.getValue()))) {
    // textDOWN = true;
    // }
    // }
    textUP = myTaskRendererImpl.isTextUp();
    textDOWN = myTaskRendererImpl.isTextDown();
    if (textUP && textDOWN) {
        rowHeight = 40;
    } else
        if (textUP) {
            rowHeight = 30;
