        Graphics g = tile.getGraphics();
        g.translate(0, (-offsety));
        g.drawImage(myTaskImage, 0, 0, null);
        myCurrentRaster = tile.getRaster().createTranslatedChild(0, (tileY * (getTileHeight())));
        myCurrentTile = tileY;
    }
    return myCurrentRaster;
}

