            processActivity(nextStarted);
        }
        if (myModel.isPrevious()) {
            for (int i = 0; i < (myPreviousStateCurrentlyProcessed.size()); i++) {
                Object next = myPreviousStateCurrentlyProcessed.get(i);
                // System.out.println (next + " : " + i);
                if (next != null) {
