        throw new RuntimeException(("Failed to create FTP document addressed by URL=" + urlAsString), e);
    } catch (MalformedURLException e) {
        if (!(GPLogger.log(e))) {
            e.printStackTrace();
        }
        throw new RuntimeException(("Failed to create FTP document addressed by URL=" + urlAsString), e);
    } catch (IOException e) {
