static File proposeOutputFile(IGanttProject project, Exporter exporter) {
    String proposedExtension = exporter.proposeFileExtension();
    if (proposedExtension == null) {
        return null;
    }
    if ((FileChooserPage.ourLastSelectedFile) != null) {
        String name = FileChooserPage.ourLastSelectedFile.getAbsolutePath();
        int lastDot = name.lastIndexOf(.);
        String extension = (lastDot >= 0) ? name.substring((lastDot + 1)) : "";
        if (extension.equals(proposedExtension)) {
            return FileChooserPage.ourLastSelectedFile;
        }
    }
    File userHome = new File(System.getProperty("user.home"));
    File result = new File(userHome, (((project.getProjectName()) + ".") + proposedExtension));
    Document projectDocument = project.getDocument();
    if (projectDocument != null) {
        File localFile = new File(projectDocument.getFilePath());
        if (localFile.exists()) {
            String name = localFile.getAbsolutePath();
            int lastDot = name.lastIndexOf(.);
            name = ((name.substring(0, lastDot)) + ".") + proposedExtension;
            result = new File(name);
        } else {
            File directory = localFile.getParentFile();
            if (directory.exists()) {
                result = new File(directory, (((project.getProjectName()) + ".") + proposedExtension));
            }
        }
    }
    return result;
}

public void setActive(boolean b) {
    if (b == false) {
        myState.setFile(getSelectedFile());
        FileChooserPage.ourLastSelectedFile = getSelectedFile();
        super.setActive(b);
    } else {
        super.setActive(b);
        File proposedFile = proposeOutputFile(myProject);
        if (proposedFile == null) {
            setSelectedFile(myState.getFile());
            return;
        }
        if (false == (proposedFile.equals(getSelectedFile()))) {
            setSelectedFile(proposedFile);
            myState.setFile(proposedFile);
        }
    }
}

void visit(Task task, int depth, StringBuffer out) throws Exception {
    Task[] nestedTasks = task.getManager().getTaskHierarchy().getNestedTasks(task);
    for (int i = 0; i < (nestedTasks.length); i++) {
        Task next = nestedTasks[i];
        String nextSerialized = serializeTask(next, depth);
        out.append(nextSerialized);
        visit(next, (depth + 1), out);
    }
}

public String visit(TaskManager taskManager) throws Exception {
    StringBuffer out = new StringBuffer();
    visit(taskManager.getTaskHierarchy().getRootTask(), 0, out);
    return out.toString();
}

private Job createTransferJob(URL baseUrl, final File file) throws IOException {
    final URL outUrl = new URL(baseUrl, file.getName());
    Job result = new Job(("transfer file " + (file.getName()))) {
        protected IStatus run(IProgressMonitor monitor) {
            byte[] buffer = new byte[((int) (file.length()))];
            FileInputStream inputStream = null;
            OutputStream outStream = null;
            try {
                inputStream = new FileInputStream(file);
                inputStream.read(buffer);
                GPLogger.log(("writing to file=" + outUrl));
                outStream = outUrl.openConnection().getOutputStream();
                outStream.write(buffer);
                outStream.flush();
                monitor.worked(1);
                return Status.OK_STATUS;
            } catch (IOException e) {
                if (!(GPLogger.log(e))) {
                    e.printStackTrace(System.err);
                }
                return Status.CANCEL_STATUS;
            } finally {
                if (inputStream != null) {
                    try {
                        inputStream.close();
                    } catch (IOException e) {
                        if (!(GPLogger.log(e))) {
                            e.printStackTrace(System.err);
                        }
                    }
                }
                if (outStream != null) {
                    try {
                        outStream.close();
                    } catch (IOException e) {
                        if (!(GPLogger.log(e))) {
                            e.printStackTrace(System.err);
                        }
                    }
                }
            }
        }
    };
    return result;
}

public void run(final File[] exportFiles, final DocumentManager.FTPOptions options, UIFacade uiFacade) {
    IJobManager jobManager = Platform.getJobManager();
    IProgressMonitor monitor = jobManager.createProgressGroup();
    Job startingJob = new Job("starting") {
        protected IStatus run(IProgressMonitor monitor) {
            monitor.beginTask("Publishing files on FTP", exportFiles.length);
            try {
                final URL baseUrl = buildURL(options);
                if (baseUrl == null) {
                    throw new RuntimeException("Failed to discover your FTP settings. Please make sure that you specified server name and user name");
                }
                for (int i = 0; i < (exportFiles.length); i++) {
                    Job nextJob = createTransferJob(baseUrl, exportFiles[i]);
                    nextJob.setProgressGroup(monitor, 1);
                    nextJob.schedule();
                    nextJob.join();
                }
                Job finishingJob = new Job("finishing") {
                    protected IStatus run(IProgressMonitor monitor) {
                        monitor.done();
                        return Status.OK_STATUS;
                    }
                };
                finishingJob.setProgressGroup(monitor, 0);
                finishingJob.schedule();
                finishingJob.join();
            } catch (IOException e) {
                if (!(GPLogger.log(e))) {
                    e.printStackTrace(System.err);
                }
            } catch (InterruptedException e) {
                if (!(GPLogger.log(e))) {
                    e.printStackTrace(System.err);
                }
            }
            return Status.OK_STATUS;
        }

        private URL buildURL(DocumentManager.FTPOptions options) {
            if ((options.getServerName().getValue()) == null) {
                return null;
            }
            StringBuffer spec = new StringBuffer("ftp://");
            boolean hasUserSpec = false;
            if ((options.getUserName().getValue()) != null) {
                spec.append(options.getUserName().getValue());
                hasUserSpec = true;
            }
            if ((options.getPassword().getValue()) != null) {
                spec.append(:).append(options.getPassword().getValue());
            }
            if (hasUserSpec) {
                spec.append(@);
            }
            spec.append(options.getServerName().getValue()).append(/);
            if ((options.getDirectoryName().getValue()) != null) {
                spec.append(options.getDirectoryName().getValue()).append(/);
            }
            try {
                URL result = new URL(spec.toString());
                return result;
            } catch (MalformedURLException e) {
                return null;
            }
        }
    };
    startingJob.setProgressGroup(monitor, 0);
    startingJob.schedule();
}

