void save(List history, /* <GanttPreviousState */
TransformerHandler handler) throws IOException, ParserConfigurationException, SAXException {
    AttributesImpl attrs = new AttributesImpl();
    startElement("previous", handler);
    for (int i = 0; i < (history.size()); i++) {
        final GanttPreviousState nextState = ((GanttPreviousState) (history.get(i)));
        /* <GanttPreviousStateTask> */
        final List stateTasks = nextState.load();
        addAttribute("name", nextState.getName(), attrs);
        startElement("previous-tasks", attrs, handler);
        // ArrayList list =
        // ((GanttPreviousState)previous.get(i)).getTasks();
        for (int j = 0; j < (stateTasks.size()); j++) {
            GanttPreviousStateTask task = ((GanttPreviousStateTask) (stateTasks.get(j)));
            addAttribute("id", String.valueOf(task.getId()), attrs);
            addAttribute("start", task.getStart().toXMLString(), attrs);
            addAttribute("duration", String.valueOf(task.getDuration()), attrs);
            addAttribute("meeting", String.valueOf(task.isMilestone()), attrs);
            addAttribute("super", String.valueOf(task.hasNested()), attrs);
            emptyElement("previous-task", attrs, handler);
        }
        endElement("previous-tasks", handler);
    }
    endElement("previous", handler);
}

void save(IGanttProject project, TransformerHandler handler) throws SAXException {
    final AttributesImpl attrs = new AttributesImpl();
    startElement("resources", handler);
    saveCustomColumnDefinitions(project, handler);
    ProjectResource[] resources = project.getHumanResourceManager().getResourcesArray();
    for (int i = 0; i < (resources.length); i++) {
        HumanResource p = ((HumanResource) (resources[i]));
        addAttribute("id", String.valueOf(p.getId()), attrs);
        addAttribute("name", p.getName(), attrs);
        addAttribute("function", p.getRole().getPersistentID(), attrs);
        addAttribute("contacts", p.getMail(), attrs);
        addAttribute("phone", p.getPhone(), attrs);
        startElement("resource", attrs, handler);
        {
            saveCustomProperties(project, p, handler);
        }
        endElement("resource", handler);
    }
    endElement("resources", handler);
}

