public String getArray() {
    String result = "";
    if ((array) != null)
        for (int i = 0; i < (array.length); i++) {
            result += (array[i]) + ",";
        }

    return (result.length()) != 0 ? result.trim().substring(0, ((result.trim().length()) - 1)) : "";
}

