            } else
                if (propertyID.equals(TaskProperties.ID_TASK_LENGTH)) {
                    sb.append(" [ ");
                    sb.append(((((int) (task.getDuration().getLength())) + " ") + (GanttLanguage.getInstance().getText("days"))));
                    sb.append(" ] ");
