                                        sb.append("}");
                                        if ((resources.length()) > 0) {
                                            sb.append(",");
                                        }
                                    }
                                    if ((resources.length()) > 0) {
                                        sb.append(resources);
                                    }
                                    sb.append(" ");
                                }
                                res = sb.toString();
                            } else
                                if (propertyID.equals(TaskProperties.ID_TASK_ID)) {
                                    sb.append("# ").append(task.getTaskID());
                                    res = sb.toString();
                                } else
                                    if (propertyID.equals(TaskProperties.ID_TASK_PREDECESSORS)) {
                                        TaskDependency[] dep = task.getDependenciesAsDependant().toArray();
                                        int i = 0;
                                        if ((dep != null) && ((dep.length) > 0)) {
