}

/**
 * Display an URL in the system browser. If you want to display a file, you
 * must include the absolute path name.
 *
 * @param url
 * 		the documents url (the url must start with either "http://"
 * 		or "file://").
 */
public static boolean displayURL(String url) {
    // Opening a browser, even when running sandbox-restricted
    // in JavaWebStart.
    try {
        Class serManClass = Class.forName("javax.jnlp.ServiceManager");
        Class basSerClass = Class.forName("javax.jnlp.BasicService");
        Class[] stringParam = new Class[]{ String.class };
        Class[] urlParam = new Class[]{ URL.class };
        Object basicService = serManClass.getMethod("lookup", stringParam).invoke(serManClass, new Object[]{ "javax.jnlp.BasicService" });
        basSerClass.getMethod("showDocument", urlParam).invoke(basicService, new Object[]{ new URL(url) });
        return true;
    } catch (Exception e) {
        // Not running in JavaWebStart or service is not supported.
        // We continue with the methods below ...
    }
    String[] cmd = null;
    switch (BrowserControl.getPlatform()) {
        case BrowserControl.WIN_ID :
            return BrowserControl.runCmdLine(BrowserControl.replaceToken(BrowserControl.WIN_CMDLINE, BrowserControl.URLTOKEN, url));
        case BrowserControl.MAC_ID :
            return BrowserControl.runCmdLine(BrowserControl.replaceToken(BrowserControl.MAC_CMDLINE, BrowserControl.URLTOKEN, url));
        default :
            for (int i = 0; i < (BrowserControl.OTHER_CMDLINES.length); i++) {
                if (BrowserControl.runCmdLine(BrowserControl.replaceToken(BrowserControl.OTHER_CMDLINES[i], BrowserControl.URLTOKEN, url), BrowserControl.replaceToken(BrowserControl.OTHER_FALLBACKS[i], BrowserControl.URLTOKEN, url)))
                    return true;

            }
    }
    return false;
}

/**
 * Try to determine whether this application is running under Windows or
 * some other platform by examing the "os.name" property.
 *
 * @return the ID of the platform
 */
private static int getPlatform() {
    String os = System.getProperty("os.name");
    if ((os != null) && (os.startsWith(BrowserControl.WIN_PREFIX)))
        return BrowserControl.WIN_ID;

    if ((os != null) && (os.startsWith(BrowserControl.MAC_PREFIX)))
        return BrowserControl.MAC_ID;

    return BrowserControl.OTHER_ID;
}

private static String[] replaceToken(String[] target, String token, String replacement) {
    if (null == target)
        return null;

    String[] result = new String[target.length];
    for (int i = 0; i < (target.length); i++)
        result[i] = target[i].replaceAll(token, replacement);

    return result;
}

private static boolean runCmdLine(String[] cmdLine) {
    return BrowserControl.runCmdLine(cmdLine, null);
}

private static boolean runCmdLine(String[] cmdLine, String[] fallBackCmdLine) {
    try {
        System.err.println((("Trying to invoke browser, cmd=" + (BrowserControl.connectStringArray(cmdLine))) + " ... "));
        Process p = Runtime.getRuntime().exec(cmdLine);
        if (null != fallBackCmdLine) {
            // wait for exit code -- if its 0, command worked,
            // otherwise we need to start fallBackCmdLine.
            int exitCode = p.waitFor();
            if (exitCode != 0) {
                System.err.println(exitCode);
                System.err.println();
                System.err.println((("Trying to invoke browser, cmd=" + (BrowserControl.connectStringArray(fallBackCmdLine))) + " ..."));
                Runtime.getRuntime().exec(fallBackCmdLine);
            }
        }
        System.err.println();
        return true;
    } catch (InterruptedException e) {
        System.err.println(("Caught: " + e));
    } catch (IOException e) {
        System.err.println(("Caught: " + e));
    }
    System.err.println();
    return false;
}

/**
 * parse a string as hew and return the corresponding color.
 */
public static Color determineColor(String hexString) {
    if (!(Pattern.matches("#[0-9abcdefABCDEF]{6}+", hexString))) {
        return GanttGraphicArea.taskDefaultColor;
    }
    int r;
    int g;
    int b;
    r = Integer.valueOf(hexString.substring(1, 3), 16).intValue();
    g = Integer.valueOf(hexString.substring(3, 5), 16).intValue();
    b = Integer.valueOf(hexString.substring(5, 7), 16).intValue();
    return new Color(r, g, b);
}

/**
 *
 *
 * @return the color as hexadecimal version like #RRGGBB
 */
public static String getColor(Color color) {
    String res = "#";
    if ((color.getRed()) <= 15)
        res += "0";

    res += Integer.toHexString(color.getRed());
    if ((color.getGreen()) <= 15)
        res += "0";

    res += Integer.toHexString(color.getGreen());
    if ((color.getBlue()) <= 15)
        res += "0";

    res += Integer.toHexString(color.getBlue());
    return res;
}

/**
 * This method tries to parse the given date according to the given locale.
 * Actually, this method tries to parse the given string with several
 * DateFormat : Short, Medium, Long and Full. Normally if you give an
 * appropriate locale in relation with the string, this method will return
 * the correct date.
 *
 * @param date
 * 		String representation of a date.
 * @param locale
 * 		Locale use to parse the date with.
 * @return A date object.
 * @throws ParseException
 * 		Exception thrown if parsing is fruitless.
 */
public static Date parseDate(String date) throws ParseException {
    DateFormat[] formats = new DateFormat[]{ GanttLanguage.getInstance().getShortDateFormat(), GanttLanguage.getInstance().getMediumDateFormat(), GanttLanguage.getInstance().getLongDateFormat() };
    // DateFormat dfFull = DateFormat.getDateInstance(DateFormat.FULL, locale);
    for (int i = 0; i < (formats.length); i++) {
        try {
            return formats[i].parse(date);
        } catch (ParseException e) {
            if ((i + 1) == (formats.length)) {
                throw e;
            }
        } catch (IllegalArgumentException e) {
            if ((i + 1) == (formats.length)) {
                throw e;
            }
        }
    }
    throw new ParseException(("Failed to parse date=" + date), 0);
}

public static File appendSuffixBeforeExtension(File f, String suffix) throws IOException {
    String filename = f.getName();
    int i = filename.lastIndexOf(FileUtil.FILE_EXTENSION_SEPARATOR);
    File containingFolder = f.getParentFile();
    File result;
    if ((i > 0) && (i < ((filename.length()) - 1))) {
        String withoutExtension = filename.substring(0, i);
        String extension = filename.substring(i);
        result = new File(containingFolder, ((withoutExtension + suffix) + extension));
    } else {
        result = new File(containingFolder, (filename + suffix));
    }
    if (!(result.exists())) {
        result.createNewFile();
    }
    return result;
}

public static String getExtension(File file) {
    int lastDot = file.getName().lastIndexOf(FileUtil.FILE_EXTENSION_SEPARATOR);
    return lastDot >= 0 ? file.getName().substring((lastDot + 1)) : file.getName();
}

public static String getFilenameWithoutExtension(File f) {
    String filename = f.getName();
    int i = filename.lastIndexOf(FileUtil.FILE_EXTENSION_SEPARATOR);
    return i >= 0 ? filename.substring(0, i) : filename;
}

public static File replaceExtension(File f, String newExtension) throws IOException {
    String filename = f.getName();
    int i = filename.lastIndexOf(FileUtil.FILE_EXTENSION_SEPARATOR);
    File containingFolder = f.getParentFile();
    File result;
    if ((i > 0) && (i < ((filename.length()) - 1))) {
        String withoutExtension = filename.substring(0, i);
        result = new File(containingFolder, ((withoutExtension + (FileUtil.FILE_EXTENSION_SEPARATOR)) + newExtension));
    } else {
        result = new File(containingFolder, ((filename + (FileUtil.FILE_EXTENSION_SEPARATOR)) + newExtension));
    }
    return result;
}

