/**
 * Compute the king moves for each square in advance.
 */
private final void precomputeKingPlies() {
    for (int i = 0; i < 64; i++) {
        long currentMask = 0L;
        int line = i & 7;
        int row = i >> 3;
        for (int currentPlyIndex = 0; currentPlyIndex < (PlyGenerator._kingPlyOffset.length); currentPlyIndex++) {
            int[] curOffsets = PlyGenerator._kingPlyOffset[currentPlyIndex];
            int targetRow = row + (curOffsets[1]);
            int targetLine = line + (curOffsets[0]);
            if ((((targetRow >= 0) && (targetRow < 8)) && (targetLine >= 0)) && (targetLine < 8)) {
                currentMask |= 1L << ((targetRow << 3) + targetLine);
            }
        }
        _kingMask[i] = currentMask;
    }
}

/**
 * Compute the knight plies for each square. This is done at startup,
 * so we can get the plies by a simple array access.
 */
private final void precomputeKnightPlies() {
    for (int i = 0; i < 64; i++) {
        long currentMask = 0L;
        int line = i & 7;
        int row = i >> 3;
        for (int currentPlyIndex = 0; currentPlyIndex < (PlyGenerator._knightPlyOffset.length); currentPlyIndex++) {
            int[] curOffsets = PlyGenerator._knightPlyOffset[currentPlyIndex];
            int targetRow = row + (curOffsets[1]);
            int targetLine = line + (curOffsets[0]);
            if ((((targetRow >= 0) && (targetRow < 8)) && (targetLine >= 0)) && (targetLine < 8)) {
                currentMask |= 1L << ((targetRow << 3) + targetLine);
            }
        }
        _knightMask[i] = currentMask;
    }
}

/**
 * Presort the scored plies.
 */
private final void presortPlies() {
    if ((_plyCounter) > 1) {
        // Check the array size, so the sort methods dont have to do it.
        quickersort(0, ((_plyCounter) - 1));
    }
}

/**
 * Perform a combined quick-/shakersort on a ply partition.
 *
 * @param l
 * 		The left bound of the array partition.
 * @param r
 * 		The right bound of the array partition.
 */
private final void quickersort(int l, int r) {
    int i = l;
    int j = r;
    AnalyzedPly x = _currentPlies[((l + r) / 2)];
    do {
        while ((_currentPlies[i].getScore()) > (x.getScore())) {
            i++;
        } 
        while ((x.getScore()) > (_currentPlies[j].getScore())) {
            j--;
        } 
        if (i <= j) {
            AnalyzedPly w = _currentPlies[i];
            _currentPlies[(i++)] = _currentPlies[j];
            _currentPlies[(j--)] = w;
        }
    } while (i <= j );
    if (l < j) {
        if ((l - j) > 7) {
            quickersort(l, j);
        } else {
            shakersort(l, j);
        }
    }
    if (i < r) {
        if ((r - i) > 7) {
            quickersort(i, r);
        } else {
            shakersort(i, r);
        }
    }
}

